import React, { useEffect, useState } from "react";
import "./UserCard.css";
import { useParams } from "react-router-dom";

const UserCard = () => {
  const [user, setUser] = useState({});
  const params = useParams();
  useEffect(() => {
    const getData = async () => {
      const response = await fetch(
        `https://jsonplaceholder.typicode.com/users/${params.id}`
      );
      const userData = await response.json();
      setUser(userData);
    };
    getData();
  }, [params.id]);

  useEffect(() => {
    console.log(user);
  }, [user]);

  return (
    <>
      <div class="card">
        <header class="card-personal-header">
          <h1 class="card-personal-name">{name}</h1>
          <a href="/" class="card-personal-link">
            {username}
          </a>
          <p class="card-personal-description">{phone}</p>
        </header>
      </div>
      ayush
    </>
  );
};
export default UserCard;
