import React, { useEffect, useState } from "react";
import UserCardList from "../UserCardList/UserCardList";
import "./UserList.css";
import CircularProgress from "@mui/joy/CircularProgress";

const UserList = () => {
  const [isloading, setIsloading] = useState(true);
  const [users, setUsers] = useState([]);
  useEffect(() => {
    try {
      //fetching the code of the users
      const getData = async () => {
        const response = await fetch(
          "https://jsonplaceholder.typicode.com/users"
        );
        const userData = await response.json();
        setUsers(userData);
        setIsloading(false);
      };
      //adding custom loading time
      setTimeout(getData, 1000);
    } catch (error) {
      console.log(error);
    }
  }, []);
  useEffect(() => {
    console.log(users);
  }, [users]);
  return (
    <div clasName="userlistMasterContainer">
      <Searchar />
      <div class="">
        {isloading ? (
          <div className="userlistloader">
            <CircularProgress size="lg" />
          </div>
        ) : (
          <div className="userlistContainer">
            {users.map((value, index) => {
              return <UserCardList value={value} />; //passing the value as a props to the usercardlist
            })}
          </div>
        )}
      </div>
    </div>
  );
};

export default UserList;
