import React, { useEffect, useState } from "react";
import "./UserCard.css";
import { useParams } from "react-router-dom";

const UserCard = () => {
  const [user, setUser] = useState({});
  const params = useParams();
  useEffect(() => {
    try {
      const getData = async () => {
        const response = await fetch(
          `https://jsonplaceholder.typicode.com/users/${params.id}`
        );
        const userData = await response.json();
        setUser(userData);
        console.log(userData);
      };
      getData();
    } catch (error) {
      console.log(error);
    }
  }, [params.id]);
  const { name, username, email, address, phone, website, company } = user;
  console.log(user);
  // const { street = "street", city, geo, zipcode } = address;
  useEffect(() => {
    console.log(user);
  }, [user]);

  return (
    <>
      <div class="userCard-Container">
        <div className="user-Card">
          <div className="user-Header">
            <div className="imgContainer">
              <img src="/user.png" alt="" />
            </div>
            <div className="userName">{name}</div>
            <div className="userNameuser">{username}</div>
          </div>
          <div className="userContent">
            <div className="userItem">
              <span class="userLabel">Email:</span>
              <span class="userValue">{email}</span>
            </div>
            <div className="userItem">
              {" "}
              <span class="userLabel">Phone:</span>
              <span class="userValue">{phone}</span>
            </div>
            <div className="userItem">
              <span class="userLabel">Website:</span>
              <span class="userValue">{website}</span>
            </div>
            {/* <div className="userItem">
              <span class="userLabel">street:</span>
              <span class="userValue">{street}</span>
            </div> */}
            {/* <div className="userItem">
              <span class="userLabel">city:</span>
              <span class="userValue">{city}</span>
            </div>
            <div className="userItem">
              <span class="userLabel">zipcode:</span>
              <span class="userValue">{zipcode}</span>
            </div>
            <div className="userItem">
              <span class="userLabel">lat:</span>
              <span class="userValue">{geo.lat}</span>
              <span style={{ marginleft: "20px" }} class="userLabel">
                long:
              </span>
              <span class="userValue">{geo.lng}</span>
            </div> */}

            <div className="userItem">
              <span class="userLabel">Company:</span>
              {/* <span class="userValue">{company.name}.</span> */}
            </div>
            <div className="userItem">
              <button className="userBtn">View</button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
};
export default UserCard;
