import React, { useEffect, useState } from "react";
import UserCardList from "../UserCardList/UserCardList";
import "./UserList.css";
import CircularProgress from "@mui/joy/CircularProgress";
import SearchBar from "../SearchBar/SearchBar";
import Pagination from "../Pagination/Pagination";

const UserList = () => {
  const [isloading, setIsloading] = useState(true);
  const [users, setUsers] = useState([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [usersPerPage] = useState(10);

  useEffect(() => {
    try {
      //fetching the code of the users
      const getData = async () => {
        const response = await fetch(
          "https://jsonplaceholder.typicode.com/users"
        );
        const userData = await response.json();
        setUsers(userData);
        setIsloading(false);
      };
      //adding custom loading time
      setTimeout(getData, 1000);
    } catch (error) {
      console.log(error);
    }
  }, []);
  useEffect(() => {
    console.log(users);
  }, [users]);

  const indexOfLastUser = currentPage * usersPerPage;
  const indexOfFirstUser = indexOfLastUser - usersPerPage;
  const currentUsers = users.slice(indexOfFirstUser, indexOfLastUser);
  const totalPages = Math.ceil(users.length / usersPerPage);

  const handlePageChange = (pageNumber) => setCurrentPage(pageNumber);

  return (
    <div className="userlistMasterContainer">
      {/* <SearchBar /> */}

      {isloading ? (
        <div className="userlistloader">
          <CircularProgress size="lg" />
        </div>
      ) : (
        <div className="userlistContainer">
          {users.map((value, index) => {
            return <UserCardList value={value} />; //passing the value as a props to the usercardlist
          })}
        </div>
      )}
      <div>
        <Pagination />
      </div>
    </div>
  );
};

export default UserList;
